mapper.select:
select A.PK_ID, A.NAME, A.DESCRIPTION, A.TYPE, A.NOTE, A.DEL_FLG, A.CRT_USER, A.CRT_TIME, A.UPT_USER, A.UPT_TIME, A.IMPL_CLASS, A.MODEL from COM_DATASOURCE A where A.DEL_FLG = '0' and A.PK_ID = #{pkId} and A.NAME like '%' || #{name} || '%' and A.DESCRIPTION like '%' || #{describe} || '%' and A.NOTE like '%' || #{note} || '%' and A.TYPE = #{type} and A.CRT_TIME >= #{crtTimeStart} and #{crtTimeEnd} >= A.CRT_TIME and A.UPT_TIME >= #{uptTimeStart} and #{uptTimeEnd} >= A.UPT_TIME and A.CRT_USER like '%' || #{crtUser} || '%' and A.UPT_USER like '%' || #{uptUser} || '%' and A.IMPL_CLASS like '%' #{implClass} || '%' and A.MODEL = #{model}
and exists (select 1 from t_gf_dict b where b.dict_type_id = #{imDsTypeDictId} and A.type = b.dict_id)
order by A.NAME asc

select A.PK_ID, A.NAME, A.DESCRIPTION, A.TYPE, A.NOTE, A.DEL_FLG, A.CRT_USER, A.CRT_TIME, A.UPT_USER,
A.UPT_TIME, A.IMPL_CLASS, A.MODEL
from COM_DATASOURCE A
where A.DEL_FLG = '0'
and A.MODEL = #{model,jdbcType=VARCHAR}
and A.NAME = #{name,jdbcType=VARCHAR}
