mapper.select:
select A.PK_ID, A.DS_ID, A.NAME, A.DESCRIBE, A.NOTE, A.DEL_FLG, A.CRT_USER, A.CRT_TIME, A.UPT_USER,
A.UPT_TIME, A.TB_NAME, A.STATUS, A.TYPE, B.NAME AS DS_NAME, B.TYPE as DS_TYPE, B.DESCRIBE as DS_DESC
from IM_METADATA A
left join com_datasource B
on A.DS_ID = B.PK_ID
where A.DEL_FLG = '0'
and A.PK_ID = #{pkId}
and A.DS_ID = #{dsId}
and A.NAME like '%' || #{name} || '%'
and A.DESCRIBE like '%' || #{describe} || '%'
and A.NOTE like '%' || #{note} || '%'
and A.TYPE = #{type}
and A.STATUS = #{status}
and A.CRT_TIME >= #{crtTimeStart}
and #{crtTimeEnd} >= A.CRT_TIME
and A.UPT_TIME >= #{uptTimeStart}
and #{uptTimeEnd} >= A.UPT_TIME
and A.CRT_USER like '%' || #{crtUser} || '%'
and A.UPT_USER like '%' || #{uptUser} || '%'
ORDER BY CRT_TIME desc
