mapper.select:
  select IM.PK_ID, IM.NAME, IM.DESCRIBE, IM.S_DS_ID, IM.T_MD_ID, IM.NOTE, IM.DEL_FLG, IM.CRT_USER,
IM.CRT_TIME, IM.UPT_USER, IM.UPT_TIME, IM.TYPE, IM.BUILD_MODE, IM.UPDATE_MODE,IM.E_DS_ID,IM.STATUS,CD.NAME SDSNAME,IMM.NAME  TDSNAME
from IM_MODEL IM
left join COM_DATASOURCE CD
on CD.PK_ID = IM.S_DS_ID
and CD.DEL_FLG = '0'
LEFT JOIN IM_METADATA IMM
on IMM.PK_ID = IM.T_MD_ID
and IMM.DEL_FLG = '0'
where IM.DEL_FLG = '0'
and IM.NAME = #{name} 
and IM.DESCRIBE = #{describe} 
and IM.T_MD_ID = #{tMdId} 
and IM.TYPE = #{type} 
and IM.S_DS_ID = #{sDsId} 
and IM.CRT_TIME >= #{crtTimeStart} 
and #{crtTimeEnd} >= IM.CRT_TIME 
and IM.UPT_TIME >= #{uptTimeStart} 
and #{uptTimeEnd} >= IM.UPT_TIME
