mapper.select:
select IM.PK_ID, IM.DS_ID, IM.NAME, IM.DESCRIPTION, IM.NOTE, IM.DEL_FLG, IM.CRT_USER, IM.CRT_TIME, IM.UPT_USER,
IM.UPT_TIME, IM.TB_NAME, IM.STATUS, IM.TYPE, CD.NAME AS DS_NAME, CD.TYPE as DS_TYPE, CD.DESCRIPTION as DS_DESC
from IM_METADATA IM
left join COM_DATASOURCE CD
on IM.DS_ID = CD.PK_ID and CD.DEL_FLG = '0'
where IM.DEL_FLG = '0'
and IM.PK_ID = #{pkId}
and IM.DS_ID = #{dsId}
and IM.NAME like '%' || #{name} || '%'
and IM.DESCRIPTION like '%' || #{describe} || '%'
and IM.NOTE like '%' || #{note} || '%'
and IM.TYPE = #{type}
and IM.STATUS = #{status}
and IM.CRT_TIME >= #{crtTimeStart}
and #{crtTimeEnd} >= IM.CRT_TIME
and IM.UPT_TIME >= #{uptTimeStart}
and #{uptTimeEnd} >= IM.UPT_TIME
and IM.CRT_USER like '%' || #{crtUser} || '%'
and IM.UPT_USER like '%' || #{uptUser} || '%'
order by IM.NAME asc
