mapper.select:
select PK_ID, APP_ID, SEQ, NAME, DESCRIPTION, TYPE, ORDER_TYPE,NOTE
from IQ_APPLICATION_ORDER_COLUMN
where PK_ID = #{pkId,jdbcType=VARCHAR}

select PK_ID, APP_ID, SEQ, NAME, DESCRIPTION, TYPE, ORDER_TYPE,NOTE
from IQ_APPLICATION_ORDER_COLUMN

  and PK_ID = #{pkId}
  and APP_ID = #{appId}
  and SEQ = #{seq}
  and NAME like '%'||#{name}||'%'
  and DESCRIPTION like '%'||#{describe}||'%'
  and TYPE = #{type}
  and ORDER_TYPE = #{orderType}

order by SEQ asc
