mapper.select:
select PK_ID, APP_ID, NAME, DESCRIPTION, TYPE, STATS, LABEL, SEQ, LENGTH,NOTE
from IQ_APPLICATION_RETURN_COLUMN
where PK_ID = #{pkId,jdbcType=VARCHAR}

select PK_ID, APP_ID, NAME, DESCRIPTION, TYPE, STATS, LABEL, SEQ, LENGTH,NOTE
from IQ_APPLICATION_RETURN_COLUMN

    and PK_ID = #{pkId}
    and APP_ID = #{appId}
    and SEQ = #{seq}
    and NAME like '%'||#{name}||'%'
    and DESCRIPTION like '%'||#{describe}||'%'
    and TYPE = #{type}
    and STATS = #{stats}
    and LENGTH = #{length}

order by SEQ asc
