mapper.select:
select PK_ID, USER_NAME, SERVICE_NAME, REQUEST_CONTENT, REQUEST_START_TIME, REQUEST_END_TIME,
RUN_START_TIME, RUN_END_TIME, RESPONSE_CONTENT, STATUS, APP_TYPE, REQUEST_TYPE, APP_NAME, ERROR_CODE,
MESSAGE, SYNC_TYPE, IS_CACHE, CONSUME_TIME
from MC_CONSUME_LOG

  and PK_ID = #{pkId}
  and USER_NAME like '%' || #{userName} || '%'
  and SERVICE_NAME like '%'|| #{serviceName} || '%'
  and REQUEST_CONTENT like '%' || #{requestContent} || '%'
  and REQUEST_START_TIME >= #{requestStartTimeStart}
  and #{requestStartTimeEnd} >= REQUEST_START_TIME
  and REQUEST_END_TIME >= #{requestEndTimeStart}
  and #{requestEndTimeEnd} >= REQUEST_END_TIME
  and RUN_START_TIME >= #{runStartTimeStart}
  and #{runStartTimeEnd} >= RUN_START_TIME
  and RUN_END_TIME >= #{runEndTimeStart}
  and #{runEndTimeEnd} >= RUN_END_TIME
  and RESPONSE_CONTENT like '%'|| #{responseContent} || '%'
  and STATUS = #{status}
  and ERROR_CODE = #{errorCode}
  and APP_TYPE = #{appType}
  and REQUEST_TYPE = #{requestType}
  and APP_NAME like '%'|| #{appName} || '%'

order by REQUEST_END_TIME desc
